  //         (key) => response.data.data[key]
  //       );
  //       console.log("arrayOfData", arrayOfData);
  //       setLanguages(arrayOfData);
  //     })
  //     .catch(function (error) {
  //       console.error(error);
  //     });
  // };

  // Backend call

  const getLanguages = async () => {
    const response = await axios("https://git.heroku.com/google-traduction.git/languages");
    setLanguages(response.data);
  };

  useEffect(() => {
    getLanguages();
  }, []);

  // const translate = () => {
  //   setLoader(true);
  //   const options = {
  //     method: "GET",
  //     url: "https://google-translate20.p.rapidapi.com/translate",
  //     params: {
  //       text: textToTranslate,
  //       tl: outputLanguage,
  //       sl: inputLanguage,
  //     },
  //     headers: {
  //       "X-RapidAPI-Host": "google-translate20.p.rapidapi.com",
  //       "X-RapidAPI-Key": "dff427a5b5mshdc656cb0e3fd1fap1a6b02jsn260cef193e33",
  //     },
  //   };

  //   axios
  //     .request(options)
  //     .then(function (response) {
  //       console.log(response.data);
  //       setTranslatedText(response.data.data.translation);
  //       setLoader(false);
  //     })
  //     .catch(function (error) {
  //       console.error(error);
  //     });
  // };
  // console.log("translate", translatedText);

  // Backend call

  const translate = async () => {
    setLoader(true);
    console.log("translate");
    const data = {
      textToTranslate,
      outputLanguage,
      inputLanguage,
    };
    const response = await axios.get("https://git.heroku.com/google-traduction.git/translation", {
      params: data,
    });
    console.log("response", response);
    setTranslatedText(response.data);
    setLoader(false);
  };

  // Func
  const handleClick = () => {
    setInputLanguage(outputLanguage);
    setOutputLanguage(inputLanguage);
  };
  return (
    <div className="app">
      {!showModal && (
        <>
          <TextBox
            style="input"
            selectedLanguage={inputLanguage}
            setShowModal={setShowModal}
            setTextToTranslate={setTextToTranslate}
            textToTranslate={textToTranslate}
          />
          <div className="arrow-container" onClick={handleClick}>
            <Arrows />
          </div>
          <TextBox
            style="output"
            selectedLanguage={outputLanguage}
            setShowModal={setShowModal}
            translatedText={translatedText}
            setTranslatedText={setTranslatedText}
          />
          {loader ? (
            <Loader />
          ) : (
            <div className="button-container" onClick={translate}>
              <Button />
            </div>
          )}
        </>
      )}
      {showModal && (
        <Modal
          setShowModal={setShowModal}
          languages={languages}
          chosenLanguage={
            showModal === "input" ? inputLanguage : outputLanguage
          }
          setChosenLanguage={
            showModal === "input" ? setInputLanguage : setOutputLanguage
          }
        />
      )}
    </div>
  );
}

export default App;
